<?phpsession_start();$conn = new mysqli('sql102.infinityfree.com', 'if0_38356551', 'x5AdReNBSY', 'if0_38356551_onikhasan); // Database credentialsif (!isset($_SESSION['user_id'])) {    header("Location: login.php");}$user_id = $_SESSION['user_id'];$ads = $conn->query("SELECT * FROM ads WHERE is_locked = 0");if ($_SERVER['REQUEST_METHOD'] == 'POST') {    $ad_id = $_POST['ad_id'];    $stmt = $conn->prepare("UPDATE ads SET is_locked = 1, locked_until = DATE_ADD(NOW(), INTERVAL 2 HOUR) WHERE id = ?");    $stmt->bind_param("i", $ad_id);    $stmt->execute();    // Update user balance    $conn->query("UPDATE users SET balance = balance + 0.10 WHERE id = $user_id");}?><!DOCTYPE html><html lang="bn"><head>    <meta charset="UTF-8">    <title>এডস দেখা</title>    <link rel="stylesheet" href="style.css"></head><body>    <header>        <h1>এডস</h1>    </header>    <main>        <h2>এড লিংকগুলো</h2>        <ul>            <?php while ($ad = $ads->fetch_assoc()): ?>                <li>                    <a href="<?php echo $ad['ad_link']; ?>" target="_blank"><?php echo $ad['ad_link']; ?></a>                    <form method="POST">                        <input type="hidden" name="ad_id" value="<?php echo $ad['id']; ?>">                        <button type="submit">এড দেখুন</button>                    </form>                </li>            <?php endwhile; ?>        </ul>    </main></body></html>